import {
  IOptions, ISettings, IEventPosition, IConfig,
} from '../interfaces/interfaces';
import View from '../view/View';
import IView from '../view/interface';
import Model from '../model/Model';
import IModel from '../model/interface';
import IController from './interface';

class Controller implements IController {
  readonly options: IOptions;

  readonly $root: JQuery<HTMLElement>;

  public view: IView;

  public model: IModel;

  public $slider: JQuery<HTMLElement>;

  private vertical: boolean;

  constructor(root: JQuery<HTMLElement>, options: IOptions) {
    this.options = options;
    this.$root = root;
    this.$slider = jQuery('<div>', { class: 'meta-slider' }).prependTo(this.$root);
    this.model = new Model(this.options);
    this.vertical = this.model.getConfig().vertical;
    this.view = new View(this.$slider, this.model.getConfig());
    this.view.observer.subscribe({ key: 'mouseMove', observer: this.moveHandle.bind(this) });
    this.view.observer.subscribe({ key: 'moveEnd', observer: this.moveEnd.bind(this) });
    this.view.observer.subscribe({ key: 'click', observer: this.clickOnScale.bind(this) });
    this.view.observer.subscribe({ key: 'position', observer: this.changePositionByTrack.bind(this) });
    this.init();
  }

  public init() {
    this.$slider.addClass(this.vertical ? 'meta-slider_vertical' : 'meta-slider_horizontal');
    const { trackStart, trackWidth } = this.view.getTrackParameters();
    this.model.setTrackParameters(trackStart, trackWidth);
    const stepsArr = this.model.initStepsArr();
    this.view.init(stepsArr);
    this.model.correctFromTo();
    const parameters = this.model.initParameters();
    this.view.setParameters(parameters);
  }

  public addPanel() {
    this.view.initPanel(this.model.getConfig());
    this.view.observer.subscribe({ key: 'setting', observer: this.changeSettings.bind(this) });
  }

  private moveHandle(options: IEventPosition) {
    const parameters = this.model.takeParamHandleMove(options);
    if (typeof parameters !== 'boolean') {
      this.view.setParameters(parameters);
    }
  }

  private moveEnd() {
    const { from, to } = this.model.correctFromToByParams();
    this.view.setSettings({ from });
    this.view.setSettings({ to });
    this.view.setParameters(this.model.getParameters());
  }

  private clickOnScale(value: number) {
    const parameters = this.model.takeParamScaleClick(value);
    if (parameters) {
      this.view.setParameters(parameters);
    }
  }

  private changeSettings(setting: ISettings) {
    this.model.setSetting(setting);
    const newConfig = this.model.getConfig();
    const key = Object.keys(setting)[0];
    switch (key) {
      case 'min':
      case 'max':
      case 'step':
        this.view.initScale(this.model.initStepsArr());
        this.model.correctFromTo();
        this.view.setParameters(this.model.initParameters());
        break;
      case 'from':
      case 'to':
        this.view.setParameters(this.model.initParameters());
        break;
      case 'range':
        this.view.correctHandlesByRange(newConfig.range);
        this.view.initTips(newConfig.tip);
        this.view.setParameters(this.model.initParameters());
        break;
      case 'tip':
        this.view.initTips(newConfig.tip);
        this.view.changeTips(this.model.getParameters());
        break;
      case 'vertical': {
        this.vertical = !this.vertical;
        this.model.setVertical(this.vertical);
        this.$slider.removeClass(this.vertical ? 'meta-slider_horizontal' : 'meta-slider_vertical');
        this.view.changeDirection(this.vertical);
        this.init();
        break;
      }
      default: {
        throw new Error('undefined setting');
      }
    }
  }

  private changePositionByTrack(position: number) {
    const parameters = this.model.takeParamTrackClick(position);
    if (parameters) {
      this.view.setParameters(parameters);
    }
  }
}

export default Controller;

import {
  IConfig, IEventPosition, IParameters, ISettings, ITrackPosition,
} from '../interfaces/interfaces';
import Panel from '../panel/Panel';
import IPanel from '../panel/interface';
import Observer from '../observer/Observer';
import IView from './interface';
import './slider.scss';

import Track from './elements/track/track';
import Scale from './elements/scale/scale';
import Handle from './elements/handle/handle';
import Interval from './elements/interval/interval';
import Tip from './elements/tip/tip';
import ITrack from './elements/track/interface';
import IScale from './elements/scale/interface';
import IHandle from './elements/handle/interface';
import IInterval from './elements/interval/interface';
import ITip from './elements/tip/interface';

class View implements IView {
  public observer: Observer;

  public scale: IScale;

  public handles: IHandle;

  public track: ITrack;

  public interval: IInterval;

  public tips: ITip;

  public panel: IPanel | undefined;

  readonly $slider: JQuery<HTMLElement>;

  readonly $container: JQuery<HTMLElement>;

  private config: IConfig;

  constructor(slider: JQuery<HTMLElement>, config: IConfig) {
    this.$slider = slider;
    this.config = config;
    this.observer = new Observer();
    this.$container = jQuery('<div>', {
      class: `meta-slider__container meta-slider__container_${this.config.vertical ? 'vertical' : 'horizontal'}`,
    }).appendTo(this.$slider);
    this.track = new Track(this.$container);
    this.track.init(this.config.vertical);
    this.scale = new Scale(this.$container);
    this.handles = new Handle(this.$container);
    this.tips = new Tip(this.$container);
    this.interval = new Interval(this.$container);
    this.panel = undefined;
  }

  public init(stepsArr: IParameters[]): void {
    this.track.init(this.config.vertical);
    this.track.observer.subscribe({ key: 'position', observer: this.changePositionByTrack.bind(this) });
    this.handles.init(this.config.vertical, this.config.range);
    this.handles.correctHandlesByRange(this.config.range);
    this.handles.observer.subscribe({ key: 'mouseMove', observer: this.mouseMove.bind(this) });
    this.handles.observer.subscribe({ key: 'moveEnd', observer: this.mouseMoveEnd.bind(this) });
    this.tips.init(this.config.tip);
    this.interval.init(this.config.vertical);
    this.scale.init(stepsArr, this.config.vertical);
    this.scale.observer.subscribe({ key: 'click', observer: this.scaleClick.bind(this) });
  }

  public getTrackParameters(): ITrackPosition {
    return this.track.getTrackParameters();
  }

  public setParameters(parameters: IParameters[]): void {
    this.handles.moveHandles(parameters);
    this.tips.changeTips(parameters);
    this.interval.moveInterval(parameters);
    if (this.panel !== undefined) {
      this.panel.initValues(parameters);
    }
  }

  public initScale(stepsArr: IParameters[]): void {
    this.scale.init(stepsArr, this.config.vertical);
  }

  public correctHandlesByRange(range:boolean): void {
    this.handles.correctHandlesByRange(range);
  }

  public initTips(tip: boolean): void {
    this.tips.init(tip);
  }

  public changeTips(parameters: IParameters[]): void {
    this.tips.changeTips(parameters);
  }

  public changeDirection(vertical: boolean): void {
    this.config.vertical = vertical;
    this.track.init(this.config.vertical);
    this.$container.removeClass(`meta-slider__container_${this.config.vertical ? 'horizontal' : 'vertical'}`).addClass(`meta-slider__container_${this.config.vertical ? 'vertical' : 'horizontal'}`);
  }

  public setSettings(setting: ISettings): void {
    if (this.panel !== undefined) {
      this.panel.setValue(setting);
    }
  }

  public initPanel(config: IConfig): void {
    this.panel = new Panel(this.$slider);
    this.panel.observer.subscribe({ key: 'setting', observer: this.changeSettings.bind(this) });
    this.panel.initPanel(config);
    this.panel.initBounds(config);
  }

  private changePositionByTrack(position: number): void {
    this.observer.notify('position', position);
  }

  private scaleClick(currentValue: number): void {
    this.observer.notify('click', currentValue);
  }

  private changeSettings(setting: ISettings): void {
    this.observer.notify('setting', setting);
  }

  private mouseMove(options: IEventPosition): void {
    this.observer.notify('mouseMove', options);
  }

  private mouseMoveEnd(event: Event): void {
    this.observer.notify('moveEnd', event);
  }
}

export default View;

import { IConfig, IParameters, ISettings } from '../interfaces/interfaces';
import Observer from '../observer/Observer';
import IObserver from '../observer/interface';
import Input from './input/Input';
import IPanel from './interface';

import './panel.scss';

class Panel implements IPanel {
  public inputs: Map<string, Input>;

  public observer: IObserver;

  readonly $root: JQuery<HTMLElement>;

  readonly $panel: JQuery<HTMLElement>;

  readonly $form: JQuery<HTMLElement>;

  constructor(root: JQuery<HTMLElement>) {
    this.$root = root;
    this.observer = new Observer();
    this.$panel = jQuery('<div>', { class: 'panel js-panel' }).appendTo(this.$root);
    this.$form = jQuery('<form>', { class: 'panel__form js-panel__form' }).appendTo(this.$panel);
    this.inputs = new Map<string, Input>();
    this.bindEventListeners();
  }

  public initPanel(config: IConfig): void {
    const element = this;
    const inputs = new Map<string, Input>();
    Object.entries(config).forEach(([key, value]) => {
      const input = new Input(this.$form, key, value);
      input.observer.subscribe({ key: 'setting', observer: element.changeSettings.bind(element) });
      inputs.set(key, input);
      if (key === 'step') {
        input.setProp('min', 0.1);
        input.setProp('step', 0.1);
      }
    });
    this.inputs = inputs;
  }

  public initBounds(config: IConfig): void {
    Object.entries(config).forEach(([key, value]) => {
      const setting: ISettings = {};
      setting[key] = value;
      this.changeBounds(setting);
    });
  }

  public initValues(parameters: IParameters[]): void {
    switch (parameters.length) {
      case 1: {
        const max = this.takeInputFromArr('max').getValue();
        this.takeInputFromArr('from').setValue(parameters[0].value);
        this.takeInputFromArr('from').setProp('max', max);
        break;
      }
      case 2: {
        this.takeInputFromArr('from').setValue(parameters[0].value);
        this.takeInputFromArr('to').setValue(parameters[1].value);
        this.takeInputFromArr('from').setProp('max', parameters[1].value);
        this.takeInputFromArr('to').setProp('min', parameters[0].value);
        break;
      }
      default: {
        throw new Error('undefined values');
      }
    }
  }

  public setValue(setting: ISettings): void {
    const key = Object.keys(setting)[0];
    const value = Object.values(setting)[0];
    const input = this.takeInputFromArr(key);
    input.setValue(value);
  }

  public takeInputFromArr(name: string): Input {
    const input = this.inputs.get(name);
    if (input) {
      return input;
    }

    throw new Error('no such input');
  }

  static handlePanelFormSubmit(): boolean {
    return false;
  }

  private bindEventListeners(): void {
    this.$form.on('submit', Panel.handlePanelFormSubmit);
  }

  private changeSettings(setting: ISettings): void {
    this.changeBounds(setting);
    this.observer.notify('setting', setting);
  }

  private changeBounds(set: ISettings): void {
    const key = Object.keys(set)[0];
    const value = Object.values(set)[0];
    const maxInput = this.takeInputFromArr('max');
    const minInput = this.takeInputFromArr('min');
    const stepInput = this.takeInputFromArr('step');
    const fromInput = this.takeInputFromArr('from');
    const toInput = this.takeInputFromArr('to');
    const rangeInput = this.takeInputFromArr('range');
    const range = Number(maxInput.getValue()) - Number(minInput.getValue());
    switch (key) {
      case 'min':
        maxInput.setProp('min', value);
        stepInput.setProp('max', range);
        fromInput.setProp('min', value);
        break;
      case 'max':
        minInput.setProp('max', value);
        stepInput.setProp('max', range);
        fromInput.setProp('max', rangeInput.getValue() ? toInput.getValue() : value);
        toInput.setProp('max', value);
        break;
      case 'step':
        fromInput.setProp('step', value);
        toInput.setProp('step', value);
        break;
      case 'from':
        toInput.setProp('min', value);
        break;
      case 'to':
        fromInput.setProp('max', value);
        break;
      case 'range': {
        const max = value ? toInput.getValue() : maxInput.getValue();
        toInput.setProp('disabled', !value);
        fromInput.setProp('max', max);
        break;
      }
      case 'vertical':
      case 'tip':
        break;
      default:
        throw new Error('undefined setting');
    }
  }
}

export default Panel;

import IObserver from './interface';

class Observer implements IObserver {
  public observers: { key: string, observer: (data: any) => void }[];

  constructor() {
    this.observers = [];
  }

  // use type any, cause any can subscribe
  public subscribe(fn: { key: string, observer: (data: any) => void }): void {
    this.observers.push(fn);
  }

  public notify(key: string, data: any): void {
    this.observers.forEach((item) => {
      if (item.key === key) {
        item.observer(data);
      }
    });
  }
}

export default Observer;

import { ISettings } from '../../interfaces/interfaces';
import Observer from '../../observer/Observer';
import IObserver from '../../observer/interface';
import IInput from './interface';
import './input.scss';

class Input implements IInput {
  public observer: IObserver;

  public $input: JQuery<HTMLElement>;

  readonly $form: JQuery<HTMLElement>;

  readonly $element: JQuery<HTMLElement>;

  private name: string;

  private type: string;

  private value: number | boolean;

  constructor(form: JQuery<HTMLElement>, key: string, value: number | boolean) {
    this.$form = form;
    this.value = value;
    this.name = key;
    this.type = (typeof value === 'number') ? 'number' : 'checkbox';
    this.$element = this.createInput();
    this.$input = this.$element.find('.js-input__field');
    this.observer = new Observer();
    this.bindEventListeners();
  }

  public getName(): string {
    return this.name;
  }

  public getValue(): number | boolean {
    const { value } = this;
    if (typeof value === 'number') {
      Number(value);
    }
    return value;
  }

  public setValue(value: number | boolean): void {
    this.value = value;
    if (typeof value === 'number') {
      this.$input.val(value);
    } else {
      this.$input.prop('checked', value);
    }
  }

  public setProp(name: string, value: number | boolean): void {
    this.$input.prop(name, value);
  }

  private createInput() {
    const input = jQuery('<div>', { class: 'input js-input' }).appendTo(this.$form);
    const label = jQuery('<label>', { class: 'input__label' }).appendTo(input);
    jQuery('<p>', { class: 'input__text', text: this.name }).appendTo(label);
    jQuery('<input>', {
      class: `input__field js-input__field input__field_with-${this.type}`,
      type: this.type,
      name: this.name,
      value: this.value,
    }).appendTo(label);
    if (this.type === 'checkbox') {
      jQuery('<span>', { class: 'input__box' }).appendTo(label);
    }
    return input;
  }

  private bindEventListeners(): void {
    this.$input.on('change keyup', this.handleInputValueChange.bind(this));
  }

  private handleInputValueChange(event: Event): void {
    switch (this.type) {
      case 'number':
        this.value = Number((<HTMLInputElement>event.target).value);
        break;
      case 'checkbox':
        this.value = (<HTMLInputElement>event.target).checked;
        break;
      default:
        break;
    }
    const setting: ISettings = {};
    setting[this.name] = this.value;
    this.observer.notify('setting', setting);
  }
}

export default Input;

import {
  IOptions, IConfig, IParameters, IEventPosition, ISettings,
} from '../interfaces/interfaces';
import IModel from './interface';

const defaults = {
  min: 10,
  max: 40,
  step: 4,
  from: 8,
  to: 24,
  vertical: false,
  tip: true,
  range: true,
};

class Model implements IModel {
  readonly options: IOptions;

  private config: IConfig;

  private stepsArr: IParameters[];

  private parameters: IParameters[];

  private trackStart: number;

  private trackWidth: number;

  constructor(options: IOptions) {
    this.options = this.correctOptionsType(options);
    this.config = $.extend({}, defaults, this.options);
    this.correctMinMax();
    this.trackStart = 0;
    this.trackWidth = 500;
    this.stepsArr = [];
    this.parameters = [];
  }

  public correctMinMax(config: IConfig = this.config): IConfig {
    const correctConfig = { ...config };
    correctConfig.max = (config.max > config.min) ? config.max : config.min;
    correctConfig.min = (config.max > config.min) ? config.min : config.max;
    correctConfig.max = (config.max === config.min) ? config.min + 10 : correctConfig.max;
    this.config = correctConfig;
    return correctConfig;
  }

  public initStepsArr(): IParameters[] {
    const { min, max, step } = this.config;
    const range = max - min;
    const stepLength = (this.trackWidth / range) * step;
    const stepsCount = Math.floor(range / step);
    const emptyArr = Array(stepsCount + 1);
    const multiplyStep = step * 10;
    let stepsArr: IParameters[] = [];
    const valuesArr = Array.from(emptyArr, (_, i) => (min + Math.round(multiplyStep * i) / 10));
    stepsArr = valuesArr.map((el, i) => ({ value: el, position: Math.round(stepLength * i) }));
    this.stepsArr = stepsArr;
    return stepsArr;
  }

  public correctFromTo(config: IConfig = this.config): IConfig {
    const correctConfig = { ...config };
    const valuesArr = this.stepsArr.map((item) => item.value);
    const from = Model.takeClosestNum(correctConfig.from, valuesArr);
    const to = Model.takeClosestNum(correctConfig.to, valuesArr);
    if (this.config.range) {
      correctConfig.from = from < to ? from : to;
      correctConfig.to = from < to ? to : from;
    } else {
      correctConfig.from = from;
    }

    this.config = correctConfig;
    return correctConfig;
  }

  public initParameters(): IParameters[] {
    const parameters: IParameters[] = [];
    const item: IParameters = {
      value: this.config.from,
      position: 0,
    };
    item.position = this.takeXByValue(item.value);
    parameters.push(item);
    if (this.config.range) {
      const secondItem: IParameters = {
        value: this.config.to,
        position: 0,
      };
      secondItem.position = this.takeXByValue(secondItem.value);
      parameters.push(secondItem);
    }

    this.parameters = parameters;
    return parameters;
  }

  public takeParamHandleMove(options: IEventPosition): IParameters[] | boolean {
    const { eventPosition } = options;
    const { index } = options;
    const position = Math.round(eventPosition - this.trackStart);
    const isInScale = (position >= 0) && (position <= this.trackWidth);
    if (isInScale) {
      const positionsArr = this.stepsArr.map((item) => item.position);
      const stepsArrClosestIndex = Model.takeClosestIndex(position, positionsArr);
      const stepsArrItem = this.stepsArr[stepsArrClosestIndex];
      this.parameters[index].value = stepsArrItem.value;
      this.parameters[index].position = stepsArrItem.position;
      return this.parameters;
    }

    return false;
  }

  public correctFromToByParams(): { from: number, to: number } {
    if (this.config.range) {
      this.config.from = Math.min(this.parameters[0].value, this.parameters[1].value);
      this.config.to = Math.max(this.parameters[0].value, this.parameters[1].value);
    } else {
      this.config.from = this.parameters[0].value;
    }
    return { from: this.config.from, to: this.config.to };
  }

  public takeParamScaleClick(value: number): IParameters[] {
    if (this.config.range) {
      const values = [this.parameters[0].value, this.parameters[1].value];
      const index = Model.takeClosestIndex(value, values);
      this.parameters[index].value = value;
      this.parameters[index].position = this.takeXByValue(value);
      return this.parameters;
    }

    this.parameters[0].value = value;
    this.parameters[0].position = this.takeXByValue(value);
    return this.parameters;
  }

  public takeParamTrackClick(position: number): IParameters[] {
    const positionsArr = this.stepsArr.map((item) => item.position);
    const closestPosition = Model.takeClosestNum(position, positionsArr);
    if (this.config.range) {
      const positions = [this.parameters[0].position, this.parameters[1].position];
      const index = Model.takeClosestIndex(closestPosition, positions);
      this.parameters[index].position = closestPosition;
      this.parameters[index].value = this.takeValueByX(closestPosition);
      return this.parameters;
    }

    this.parameters[0].position = closestPosition;
    this.parameters[0].value = this.takeValueByX(closestPosition);
    return this.parameters;
  }

  public getConfig(): IConfig {
    return this.config;
  }

  public setConfig(config: IConfig): void {
    this.config = config;
  }

  public setSetting(setting: ISettings): void {
    this.config = $.extend({}, this.config, setting);
  }

  public getParameters(): IParameters[] {
    return this.parameters;
  }

  public setVertical(vertical: boolean): void {
    this.config.vertical = vertical;
  }

  public setTrackParameters(trackStart: number, trackWidth: number | undefined): void {
    this.trackStart = trackStart;
    this.trackWidth = trackWidth === undefined ? 500 : trackWidth;
  }

  public getStepsArr(): IParameters[] {
    return this.stepsArr;
  }

  static takeClosestNum(num: number, array: number[]): number {
    const indexOfClosest = Model.takeClosestIndex(num, array);
    return array[indexOfClosest];
  }

  static takeClosestIndex(num: number, array: number[]): number {
    const closest = array.reduce((a, b) => (Math.abs(b - num) < Math.abs(a - num) ? b : a));
    return array.indexOf(closest);
  }

  private correctOptionsType(options: IOptions = this.options):IOptions {
    const correctOptions = { ...options };
    correctOptions.max = Number.isInteger(options.max) ? Math.round(options.max) : defaults.max;
    correctOptions.min = Number.isInteger(options.min) ? Math.round(options.min) : defaults.min;
    correctOptions.step = Number.isInteger(options.step) ? Math.round(options.step) : defaults.step;
    correctOptions.from = Number.isInteger(options.from) ? Math.round(options.from) : defaults.from;
    correctOptions.to = Number.isInteger(options.to) ? Math.round(options.to) : defaults.to;
    correctOptions.vertical = typeof options.vertical === 'boolean' ? options.vertical : defaults.vertical;
    correctOptions.tip = typeof options.tip === 'boolean' ? options.tip : defaults.tip;
    correctOptions.range = typeof options.range === 'boolean' ? options.range : defaults.range;
    return correctOptions;
  }

  private takeXByValue(val: number): number {
    const item = this.stepsArr.find((el) => el.value === val);
    if (item) {
      return item.position;
    }

    throw new Error('position for this value is not consist');
  }

  private takeValueByX(x: number): number {
    const item = this.stepsArr.find((el) => el.position === x);
    if (item) {
      return item.value;
    }

    throw new Error('value for this position is not consist');
  }
}

export default Model;

import Observer from '../../../observer/Observer';
import IObserver from '../../../observer/interface';
import IHandle from './interface';
import { IParameters } from '../../../interfaces/interfaces';

class Handle implements IHandle {
  public observer: IObserver;

  readonly $slider: JQuery<HTMLElement>;

  readonly $track: JQuery<HTMLElement>;

  private vertical: boolean;

  private handles: JQuery<HTMLElement>[];

  constructor(slider: JQuery<HTMLElement>) {
    this.$slider = slider;
    this.$track = this.$slider.find('.meta-slider__track');
    this.vertical = false;
    this.observer = new Observer();
    this.handles = this.initHandles();
  }

  public init(vertical: boolean, range: boolean): void {
    this.vertical = vertical;
    this.correctHandlesByRange(range);
  }

  public correctHandlesByRange(range: boolean): void {
    if (range && this.handles.length === 1) {
      const $handle2 = jQuery('<div>', {
        class: 'meta-slider__handle meta-slider__handle_right',
      }).appendTo(this.$track);
      this.handles.push($handle2);
    } else if (!range && this.handles.length === 2) {
      const $handle2 = this.handles[1];
      $handle2.remove();
      this.handles.pop();
    }
    this.bindEventListeners();
  }

  public moveHandles(parameters: IParameters[]): void {
    this.handles.forEach((item, index) => {
      item.css(this.vertical ? 'top' : 'left', `${parameters[index].position - 20 / 2}px`);
      item.css(this.vertical ? 'left' : 'top', '-5px');
    });
  }

  public setVertical(vertical: boolean): void {
    this.vertical = vertical;
  }

  public getVertical(): boolean {
    return this.vertical;
  }

  public getHandles(): JQuery<HTMLElement>[] {
    return this.handles;
  }

  static handleDragStart(): boolean {
    return false;
  }

  private bindEventListeners(): void {
    this.handles.forEach((item) => {
      item.on('mousedown touchstart', this.handleHandleMouseDown.bind(this));
    });
  }

  private handleHandleMouseDown(event: Event): void {
    event.preventDefault();
    const eventTarget = <Element>event.target;
    const index = $(eventTarget).hasClass('meta-slider__handle_left') ? 0 : 1;
    $(document).on('mousemove', this.handleMouseMove.bind(this, index));
    $(document).on('touchmove', this.handleTouchMove.bind(this, index));
    $(document).on('mouseup touchend', this.handleMoveEnd.bind(this));
    $(document).on('dragstart', Handle.handleDragStart);
  }

  private handleMouseMove(index: number, event: Event): void {
    const eventPosition = this.vertical ? (<MouseEvent>event).pageY : (<MouseEvent>event).pageX;
    const options = { eventPosition, index };
    this.observer.notify('mouseMove', options);
  }

  private handleTouchMove(index: number, event: Event): void {
    const touches = (<TouchEvent>event)?.touches;
    if (touches !== undefined) {
      const touch = touches[0];
      const eventPosition = this.vertical ? touch.pageY : touch.pageX;
      const options = { eventPosition, index };
      this.observer.notify('mouseMove', options);
    }
  }

  private handleMoveEnd(event: Event): void {
    this.observer.notify('moveEnd', 0);
    $(document).off('mousemove mouseup touchmove touchend');
  }

  private initHandles(): JQuery<HTMLElement>[] {
    const handles: JQuery<HTMLElement>[] = [];
    const $handle1 = jQuery('<div>', {
      class: 'meta-slider__handle meta-slider__handle_left',
    }).appendTo(this.$track);
    handles.push($handle1);
    const $handle2 = jQuery('<div>', {
      class: 'meta-slider__handle meta-slider__handle_right',
    }).appendTo(this.$track);
    handles.push($handle2);
    return handles;
  }
}

export default Handle;

import { IParameters } from '../../../interfaces/interfaces';
import IInterval from './interface';

class Interval implements IInterval {
  readonly $slider: JQuery<HTMLElement>;

  readonly $interval: JQuery<HTMLElement>;

  readonly $track: JQuery<HTMLElement>;

  private vertical: boolean;

  constructor(slider: JQuery<HTMLElement>) {
    this.$slider = slider;
    this.$track = $(this.$slider).find('.meta-slider__track');
    this.$interval = jQuery('<div>', { class: 'meta-slider__interval' }).appendTo(this.$track);
    this.vertical = false;
  }

  public init(vertical: boolean): void {
    this.vertical = vertical;
  }

  public moveInterval(parameters: IParameters[]): void {
    let min: number;
    let width: number;
    const handleWidth = 20;
    const gap = 2; // to make a gap between interval and handle
    if (parameters.length === 1) {
      min = 0;
      width = parameters[0].position - handleWidth / 2 - gap;
    } else if (parameters.length === 2) {
      min = Math.min(parameters[0].position, parameters[1].position) + handleWidth / 2;
      width = Math.abs(parameters[1].position - parameters[0].position) - handleWidth - gap;
    } else {
      throw new Error('wrong number of handles');
    }

    width = (width > 0) ? width : 0;
    this.$interval.css(this.vertical ? 'height' : 'width', `${width}px`);
    this.$interval.css(this.vertical ? 'width' : 'height', '10px');
    this.$interval.css(this.vertical ? 'top' : 'left', `${min}px`);
    this.$interval.css(this.vertical ? 'left' : 'top', '0px');
  }
}

export default Interval;

import { IParameters } from '../../../interfaces/interfaces';
import Observer from '../../../observer/Observer';
import IObserver from '../../../observer/interface';
import IScale from './interface';

class Scale implements IScale {
  public observer: IObserver;

  public $slider: JQuery<HTMLElement>;

  public $scale: JQuery<HTMLElement>;

  private stepsArr: IParameters[];

  private itemWidth: number;

  private vertical: boolean;

  constructor(slider: JQuery<HTMLElement>) {
    this.$slider = slider;
    this.vertical = false;
    this.observer = new Observer();
    this.$scale = jQuery('<div>', { class: 'meta-slider__scale' }).appendTo(this.$slider);
    this.stepsArr = [];
    this.itemWidth = 20;
  }

  public init(stepsArr: IParameters[], vertical: boolean): void {
    this.vertical = vertical;
    this.stepsArr = stepsArr;
    this.$scale.empty();
    this.itemWidth = this.takeWidth();
    this.addValues();
    this.bindEventListeners();
  }

  private takeWidth(): number {
    const widthArr: number[] = [];
    const randomItemsArr = [...this.stepsArr.slice(0, 3), ...this.stepsArr.slice(-3)];
    randomItemsArr.forEach((item) => {
      const scaleItem = jQuery('<div>', { text: item.value }).appendTo(this.$scale);
      scaleItem.css(this.vertical ? 'height' : 'width', 'min-content');
      const itemWidth = this.vertical ? $(scaleItem).height() : $(scaleItem).width();
      if (itemWidth !== undefined) {
        widthArr.push(itemWidth);
      } else {
        throw new Error('wrong width of item');
      }
      scaleItem.remove();
    });
    return Math.max.apply(null, widthArr);
  }

  private addValues(): void {
    const scaleArr = this.correctScaleArr();
    const lastItemPosition = Number(scaleArr[scaleArr.length - 1].position);
    const prevLastItemPosition = Number(scaleArr[scaleArr.length - 2].position);
    if (Math.abs(prevLastItemPosition - lastItemPosition) < this.itemWidth) {
      scaleArr.splice((scaleArr.length - 2), 1);
    }
    scaleArr.forEach((item) => {
      this.addItem(item);
    });
  }

  private correctScaleArr(): IParameters[] {
    const maxStepsCount = this.takeMaxStepsCount();
    const scaleStep = Math.round(this.stepsArr.length / maxStepsCount);
    const isStepsArrSmall = this.stepsArr.length < maxStepsCount;
    const scaleArr: IParameters[] = this.stepsArr.filter((item, i) => {
      const isLineInStep = (i % scaleStep === 0) || (i === (this.stepsArr.length - 1));
      if (isLineInStep || isStepsArrSmall) {
        return item;
      } return false;
    });
    return scaleArr;
  }

  private takeMaxStepsCount(): number {
    const scaleWidth = this.vertical ? this.$scale.height() : this.$scale.width();
    if (scaleWidth !== undefined) {
      const stepsCount = scaleWidth / (this.itemWidth + 4);
      return Math.floor(stepsCount);
    }
    throw new Error('wrong scale width');
  }

  private addItem(item: IParameters): JQuery<HTMLElement> {
    const position = item.position - this.itemWidth / 2;
    const scaleItem = jQuery('<div>', {
      class: 'meta-slider__scale-item',
      style: this.vertical ? `top: ${position}px` : `left: ${position}px`,
    });
    jQuery('<div>', {
      class: 'meta-slider__line',
      text: this.vertical ? '\u2014' : '|',
    }).appendTo(scaleItem);
    jQuery('<div>', {
      class: 'meta-slider__value js-meta-slider__value',
      'data-value': item.value,
      text: item.value,
      style: this.vertical ? `height: ${this.itemWidth}px` : `width: ${this.itemWidth}px`,
    }).appendTo(scaleItem);
    scaleItem.appendTo(this.$scale);
    return scaleItem;
  }

  private bindEventListeners(): void {
    $(this.$scale).on('click touchstart', this.sendScaleClickValue.bind(this));
  }

  private sendScaleClickValue(event: Event): void {
    const { observer } = this;
    if ((<HTMLInputElement>event.target).dataset.value) {
      const currentValue = Number((<HTMLInputElement>event.target).dataset.value);
      observer.notify('click', currentValue);
    }
  }
}

export default Scale;

import { IParameters } from '../../../interfaces/interfaces';
import ITip from './interface';

class Tip implements ITip {
  readonly $slider: JQuery<HTMLElement>;

  readonly $tip: JQuery<HTMLElement>;

  private $tips: JQuery<HTMLElement>[];

  constructor(slider: JQuery<HTMLElement>) {
    this.$slider = slider;
    this.$tips = [];
    this.$tip = jQuery('<div>', { class: 'meta-slider__tip' });
  }

  public init(tip: boolean = true): void {
    const element = this;
    this.$slider.find('.meta-slider__tip').remove();
    this.$tips = [];
    if (tip) {
      const $handles = this.$slider.find('.meta-slider__handle');
      $handles.each(function () {
        const $tipClone = element.$tip.clone();
        $tipClone.appendTo($(this));
        element.$tips.push($tipClone);
      });
    }
  }

  public changeTips(parameters: IParameters[]): void {
    this.$tips.forEach((item, index) => {
      item.html(`${parameters[index].value}`);
    });
  }
}

export default Tip;
