import { IOptions, ISettings, IEventPosition } from '../interfaces/interfaces';
import View from '../view/View';
import IView from '../view/interface';
import Model from '../model/Model';
import IModel from '../model/interface';
import IController from './interface';

class Controller implements IController {
  readonly options: IOptions;

  readonly $root: JQuery<HTMLElement>;

  private vertical: boolean;

  private model: IModel;

  private view: IView;

  constructor(root: JQuery<HTMLElement>, options: IOptions) {
    this.options = options;
    this.$root = root;
    this.vertical = (options.vertical !== undefined) ? options.vertical : false;
    this.view = new View(this.$root, this.vertical);
    const { trackStart, trackWidth } = this.view.getTrackParameters();
    this.model = new Model(this.options, trackStart, trackWidth);
    this.init();
  }

  private init() {
    this.initElements();
    this.view.observer.subscribe({ key: 'mouseMove', observer: this.moveHandle.bind(this) });
    this.view.observer.subscribe({ key: 'moveEnd', observer: this.moveEnd.bind(this) });
    this.view.observer.subscribe({ key: 'click', observer: this.clickOnScale.bind(this) });
    this.view.initPanel(this.model.getConfig());
    this.view.observer.subscribe({ key: 'setting', observer: this.changeSettings.bind(this) });
    this.view.observer.subscribe({ key: 'position', observer: this.changePositionByTrack.bind(this) });
  }

  private initElements() {
    const stepsArr = this.model.getStepsArr();
    this.view.initScale(stepsArr);
    const { range } = this.model.getConfig();
    this.view.correctHandlesByRange(range);
    const { tip } = this.model.getConfig();
    this.view.initTips(tip);
    const parameters = this.model.getParameters();
    this.view.setParameters(parameters);
  }

  private moveHandle(options: IEventPosition) {
    const parameters = this.model.takeParamHandleMove(options);
    if (typeof parameters !== 'boolean') {
      this.view.setParameters(parameters);
    }
  }

  private moveEnd() {
    this.model.correctFromToByParams();
    const modelConfig = this.model.getConfig();
    this.view.setSettings({ from: modelConfig.from });
    this.view.setSettings({ to: modelConfig.to });
    this.view.setParameters(this.model.getParameters());
  }

  private clickOnScale(value: number) {
    const parameters = this.model.takeParamScaleClick(value);
    if (parameters) {
      this.view.setParameters(parameters);
    }
  }

  private changeSettings(setting: ISettings) {
    const modelConfig = this.model.getConfig();
    const newConfig = $.extend({}, modelConfig, setting);
    this.model.setConfig(newConfig);
    const key = Object.keys(setting)[0];
    switch (key) {
      case 'min':
      case 'max':
      case 'step':
        this.model.correctMinMax();
        this.view.initScale(this.model.initStepsArr());
        this.view.setSettings({ min: newConfig.min });
        this.view.setSettings({ max: newConfig.max });
        this.view.setSettings({ step: newConfig.step });
        this.model.correctFromTo();
        this.view.setParameters(this.model.initParameters());
        break;
      case 'from':
      case 'to':
        this.model.correctFromTo();
        this.view.setSettings({ from: newConfig.from });
        this.view.setSettings({ to: newConfig.to });
        this.view.setParameters(this.model.initParameters());
        break;
      case 'range':
        this.model.correctFromTo();
        this.view.setSettings({ range: newConfig.range });
        this.view.correctHandlesByRange(newConfig.range);
        this.view.initTips(newConfig.tip);
        this.view.setParameters(this.model.initParameters());
        break;
      case 'tip':
        this.view.setSettings({ tip: newConfig.tip });
        this.view.initTips(newConfig.tip);
        this.view.changeTips(this.model.getParameters().values);
        break;
      case 'vertical': {
        this.view.setSettings({ vertical: newConfig.vertical });
        this.vertical = !this.vertical;
        this.view.changeDirection(this.vertical);
        const { trackStart, trackWidth } = this.view.getTrackParameters();
        this.model.setTrackStart(trackStart);
        this.model.setTrackWidth(trackWidth);
        this.model.initStepsArr();
        this.initElements();
        break;
      }
      default: {
        throw new Error('undefined setting');
      }
    }
  }

  private changePositionByTrack(position: number) {
    const parameters = this.model.takeParamTrackClick(position);
    if (parameters) {
      this.view.setParameters(parameters);
    }
  }
}

export default Controller;

import {
  IConfig, IEventPosition, IParameters, IStepsArr, ISettings, ITrackPosition,
} from '../interfaces/interfaces';
import Panel from '../panel/Panel';
import IPanel from '../panel/interface';
import Observer from '../observer/Observer';
import IView from './interface';
import './slider.scss';

import Track from './elements/track/track';
import Scale from './elements/scale/scale';
import Handle from './elements/handle/handle';
import Interval from './elements/interval/interval';
import Tip from './elements/tip/tip';
import ITrack from './elements/track/interface';
import IScale from './elements/scale/interface';
import IHandle from './elements/handle/interface';
import IInterval from './elements/interval/interface';
import ITip from './elements/tip/interface';

class View implements IView {
  public observer: Observer;

  readonly $root: JQuery<HTMLElement>;

  readonly $container: JQuery<HTMLElement>;

  readonly $slider: JQuery<HTMLElement>;

  private vertical: boolean;

  private panel: IPanel;

  private track: ITrack;

  private scale: IScale;

  private handles: IHandle;

  private interval: IInterval;

  private tips: ITip;

  constructor(root: JQuery<HTMLElement>, vertical: boolean) {
    this.$root = root;
    this.vertical = vertical;
    this.observer = new Observer();
    this.$container = this.$root.closest('.js-body__container').addClass(this.vertical ? 'body__container_vertical' : 'body__container_horizontal');
    this.$slider = jQuery('<div>', {
      class: 'meta-slider',
    }).appendTo(this.$root).addClass(this.vertical ? 'meta-slider_vertical' : 'meta-slider_horizontal');
    this.track = new Track(this.$slider, this.vertical);
    this.track.observer.subscribe({ key: 'position', observer: this.changePositionByTrack.bind(this) });
    this.scale = new Scale(this.$slider, this.vertical);
    this.scale.observer.subscribe({ key: 'click', observer: this.scaleClick.bind(this) });
    this.handles = new Handle(this.$slider, this.vertical);
    this.handles.observer.subscribe({ key: 'mouseMove', observer: this.mouseMove.bind(this) });
    this.handles.observer.subscribe({ key: 'moveEnd', observer: this.mouseMoveEnd.bind(this) });
    this.tips = new Tip(this.$slider);
    this.interval = new Interval(this.$slider);
    this.panel = new Panel(this.$container);
    this.panel.observer.subscribe({ key: 'setting', observer: this.changeSettings.bind(this) });
  }

  public getTrackParameters(): ITrackPosition {
    return this.track.getTrackParameters();
  }

  public initScale(stepsArr: IStepsArr[]): void {
    this.scale.initScale(stepsArr, this.vertical);
  }

  public correctHandlesByRange(range:boolean): void {
    this.handles.correctHandlesByRange(range);
  }

  public initTips(tip: boolean): void {
    this.tips.initTips(tip);
  }

  public changeTips(values: number[]): void {
    this.tips.changeTips(values);
  }

  public changeDirection(vertical: boolean): void {
    this.vertical = vertical;
    this.track.setVertical(vertical);
    this.scale.setVertical(vertical);
    this.handles.setVertical(vertical);
    this.$container.removeClass(`body__container_${this.vertical ? 'horizontal' : 'vertical'}`).addClass(`body__container_${this.vertical ? 'vertical' : 'horizontal'}`);
    this.$slider.removeClass(`meta-slider_${this.vertical ? 'horizontal' : 'vertical'}`).addClass(`meta-slider_${this.vertical ? 'vertical' : 'horizontal'}`);
  }

  public setParameters(parameters: IParameters): void {
    this.handles.moveHandles(parameters.positions);
    this.tips.changeTips(parameters.values);
    this.interval.moveInterval(parameters.positions, this.vertical);
    this.panel.initValues(parameters.values);
  }

  public setSettings(setting: ISettings): void {
    this.panel.setValue(setting);
  }

  public initPanel(config: IConfig): void {
    this.panel.initPanel(config);
  }

  private changePositionByTrack(position: number): void {
    this.observer.notify('position', position);
  }

  private scaleClick(currentValue: number): void {
    this.observer.notify('click', currentValue);
  }

  private changeSettings(setting: ISettings): void {
    this.observer.notify('setting', setting);
  }

  private mouseMove(options: IEventPosition): void {
    this.observer.notify('mouseMove', options);
  }

  private mouseMoveEnd(event: Event): void {
    this.observer.notify('moveEnd', event);
  }
}

export default View;

import { IConfig, ISettings } from '../interfaces/interfaces';
import Observer from '../observer/Observer';
import IObserver from '../observer/interface';
import Input from './input/Input';
import IPanel from './interface';

import './panel.scss';

class Panel implements IPanel {
  public inputs: Map<string, Input>;

  public observer: IObserver;

  readonly $root: JQuery<HTMLElement>;

  readonly $panel: JQuery<HTMLElement>;

  readonly $form: JQuery<HTMLElement>;

  private from: Input;

  private to: Input;

  private range: Input;

  private max: Input;

  private min: Input;

  private step: Input;

  constructor(root: JQuery<HTMLElement>) {
    this.$root = root;
    this.observer = new Observer();
    this.$panel = $(this.$root).find('.js-panel');
    this.$form = this.$panel.find('.js-panel__form');
    this.inputs = this.initInputs();
    this.max = this.takeInputFromArr('max');
    this.min = this.takeInputFromArr('min');
    this.step = this.takeInputFromArr('step');
    this.from = this.takeInputFromArr('from');
    this.to = this.takeInputFromArr('to');
    this.range = this.takeInputFromArr('range');
    this.bindEventListeners();
  }

  public initPanel(config: IConfig): void {
    const element = this;
    Object.entries(config).forEach(([key, value]) => {
      const input = element.inputs.get(key);
      const setting: ISettings = {};
      if (input) {
        input.setValue(value);
        setting[key] = value;
        this.changeBounds(setting);
      }
    });
  }

  public initValues(values: number[]): void {
    switch (values.length) {
      case 1: {
        const max = this.max.getValue();
        this.from.setValue(values[0]);
        this.from.setProp('max', max);
        break;
      }
      case 2: {
        this.from.setValue(values[0]);
        this.to.setValue(values[1]);
        this.from.setProp('max', values[1]);
        this.to.setProp('min', values[0]);
        break;
      }
      default: {
        throw new Error('undefined values');
      }
    }
  }

  public setValue(setting: ISettings): void {
    const key = Object.keys(setting)[0];
    const value = Object.values(setting)[0];
    const input = this.takeInputFromArr(key);
    input.setValue(value);
  }

  static handlePanelFormSubmit(): boolean {
    return false;
  }

  private initInputs(): Map<string, Input> {
    const element = this;
    const inputs = new Map<string, Input>();
    this.$panel.find('.js-input__field').each(function () {
      const input = new Input($(this));
      const name = input.getName();
      input.observer.subscribe({ key: 'setting', observer: element.changeSettings.bind(element) });
      inputs.set(name, input);
    });
    return inputs;
  }

  private takeInputFromArr(name: string): Input {
    const input = this.inputs.get(name);
    if (input) {
      return input;
    }

    throw new Error('no such input');
  }

  private bindEventListeners(): void {
    this.$form.on('submit', Panel.handlePanelFormSubmit);
  }

  private changeSettings(setting: ISettings): void {
    this.changeBounds(setting);
    this.observer.notify('setting', setting);
  }

  private changeBounds(set: ISettings): void {
    const key = Object.keys(set)[0];
    const value = Object.values(set)[0];
    const range = Number(this.max.getValue()) - Number(this.min.getValue());
    switch (key) {
      case 'min':
        this.max.setProp('min', value);
        this.step.setProp('max', range);
        this.from.setProp('min', value);
        break;
      case 'max':
        this.min.setProp('max', value);
        this.step.setProp('max', range);
        this.from.setProp('max', this.range.getValue() ? this.to.getValue() : value);
        this.to.setProp('max', value);
        break;
      case 'step':
        this.from.setProp('step', value);
        this.to.setProp('step', value);
        break;
      case 'from':
        this.to.setProp('min', value);
        break;
      case 'to':
        this.from.setProp('max', value);
        break;
      case 'range': {
        const max = value ? this.to.getValue() : this.max.getValue();
        this.to.setProp('disabled', !value);
        this.from.setProp('max', max);
        break;
      }
      case 'vertical':
      case 'tip':
        break;
      default:
        throw new Error('undefined setting');
    }
  }
}

export default Panel;

import IObserver from './interface';

class Observer implements IObserver {
  public observers: { key: string, observer: (data: any) => void }[];

  constructor() {
    this.observers = [];
  }

  // use type any, cause any can subscribe
  public subscribe(fn: { key: string, observer: (data: any) => void }): void {
    this.observers.push(fn);
  }

  public notify(key: string, data: any): void {
    this.observers.forEach((item) => {
      if (item.key === key) {
        item.observer(data);
      }
    });
  }
}

export default Observer;

import { ISettings } from '../../interfaces/interfaces';
import Observer from '../../observer/Observer';
import IObserver from '../../observer/interface';
import IInput from './interface';
import './input.scss';

class Input implements IInput {
  public observer: IObserver;

  readonly element: JQuery<HTMLElement>;

  private name: string;

  private type: string;

  private value: number | boolean;

  constructor(element: JQuery<HTMLElement>) {
    this.element = element;
    this.observer = new Observer();
    this.name = this.element.prop('name');
    this.type = this.element.prop('type');
    this.value = (this.type === 'number') ? this.element.val() : this.element.prop('checked');
    this.bindEventListeners();
  }

  public getName(): string {
    return this.name;
  }

  public getValue(): number | boolean {
    const { value } = this;
    if (typeof value === 'number') {
      Number(value);
    }
    return value;
  }

  public setValue(value: number | boolean): void {
    this.value = value;
    if (typeof value === 'number') {
      this.element.val(value);
    } else {
      this.element.prop('checked', value);
    }
  }

  public setProp(name: string, value: number | boolean): void {
    this.element.prop(name, value);
  }

  private bindEventListeners(): void {
    $(this.element).on('change keyup', this.handleInputValueChange.bind(this));
  }

  private handleInputValueChange(event: Event): void {
    switch (this.type) {
      case 'number':
        this.value = Number((<HTMLInputElement>event.target).value);
        break;
      case 'checkbox':
        this.value = (<HTMLInputElement>event.target).checked;
        break;
      default:
        break;
    }
    const setting: ISettings = {};
    setting[this.name] = this.value;
    this.observer.notify('setting', setting);
  }
}

export default Input;

import {
  IOptions, IConfig, IParameters, IStepsArr, IEventPosition,
} from '../interfaces/interfaces';
import IModel from './interface';

const defaults = {
  min: 10,
  max: 40,
  step: 4,
  from: 8,
  to: 24,
  vertical: false,
  tip: true,
  range: true,
};

class Model implements IModel {
  readonly options: IOptions;

  private config: IConfig;

  private stepsArr: IStepsArr[];

  private parameters: IParameters;

  private trackStart: number;

  private trackWidth: number;

  constructor(options: IOptions, trackStart: number = 0, trackWidth: number = 500) {
    this.trackStart = trackStart;
    this.trackWidth = trackWidth;
    this.options = this.correctOptionsType(options);
    this.config = $.extend({}, defaults, this.options);
    this.correctMinMax();
    this.stepsArr = this.initStepsArr();
    this.correctFromTo();
    this.parameters = this.initParameters();
  }

  public correctMinMax(config: IConfig = this.config): IConfig {
    const correctConfig = { ...config };
    correctConfig.max = (config.max > config.min) ? config.max : config.min;
    correctConfig.min = (config.max > config.min) ? config.min : config.max;
    correctConfig.max = (config.max === config.min) ? config.min + 10 : correctConfig.max;
    this.config = correctConfig;
    return correctConfig;
  }

  public initStepsArr(): IStepsArr[] {
    const { min, max, step } = this.config;
    const range = max - min;
    const stepLength = (this.trackWidth / range) * step;
    const stepsCount = Math.floor(range / step);
    const emptyArr = Array(stepsCount + 1);
    const valuesArr = Array.from(emptyArr, (_, i) => (Model.round(min + Model.round(step * i))));
    const stepsArr: IStepsArr[] = [];
    valuesArr.map((el, index) => stepsArr.push({ value: el, x: Math.round(stepLength * index) }));
    if (valuesArr.indexOf(max) === -1) {
      valuesArr.push(max);
      stepsArr.push({ value: max, x: this.trackWidth });
    }

    this.stepsArr = stepsArr;
    return stepsArr;
  }

  public correctFromTo(config: IConfig = this.config): IConfig {
    const correctConfig = { ...config };
    const valuesArr = this.stepsArr.map((item) => item.value);
    const from = Model.takeClosestNum(correctConfig.from, valuesArr);
    const to = Model.takeClosestNum(correctConfig.to, valuesArr);
    if (this.config.range) {
      correctConfig.from = from < to ? from : to;
      correctConfig.to = from < to ? to : from;
    } else {
      correctConfig.from = from;
    }

    this.config = correctConfig;
    return correctConfig;
  }

  public initParameters(): IParameters {
    const parameters: IParameters = { values: [], positions: [] };
    const element = this;
    parameters.values[0] = this.config.from;
    if (this.config.range) {
      parameters.values[1] = this.config.to;
    }

    parameters.positions = parameters.values.map((x) => element.takeXByValue(x));
    this.parameters = parameters;
    return parameters;
  }

  public takeParamHandleMove(options: IEventPosition): IParameters | boolean {
    const { eventPosition } = options;
    const { index } = options;
    const position = Math.round(eventPosition - this.trackStart);
    const isInScale = (position >= 0) && (position <= this.trackWidth);
    if (isInScale) {
      const positionsArr = this.stepsArr.map((item) => item.x);
      const stepsArrClosestIndex = Model.takeClosestIndex(position, positionsArr);
      const stepsArrItem = this.stepsArr[stepsArrClosestIndex];
      this.parameters.values[index] = stepsArrItem.value;
      this.parameters.positions[index] = stepsArrItem.x;
      return this.parameters;
    }

    return false;
  }

  public correctFromToByParams(): void {
    this.parameters.values.sort();
    this.parameters.positions.sort();
    const value = this.parameters.values[0];
    this.config.from = value;
    this.config.to = this.parameters.values[1] ? this.parameters.values[1] : this.config.to;
  }

  public takeParamScaleClick(value: number): IParameters {
    if (this.config.range) {
      const index = Model.takeClosestIndex(value, this.parameters.values);
      this.parameters.values[index] = value;
      this.parameters.positions[index] = this.takeXByValue(value);
      return this.parameters;
    }

    this.parameters.values = [value];
    this.parameters.positions = [this.takeXByValue(value)];
    return this.parameters;
  }

  public takeParamTrackClick(position: number): IParameters {
    const positionsArr = this.stepsArr.map((item) => item.x);
    const closestPosition = Model.takeClosestNum(position, positionsArr);
    if (this.config.range) {
      const index = Model.takeClosestIndex(closestPosition, this.parameters.positions);
      this.parameters.positions[index] = closestPosition;
      this.parameters.values[index] = this.takeValueByX(closestPosition);
      return this.parameters;
    }

    this.parameters.positions = [closestPosition];
    this.parameters.values = [this.takeValueByX(closestPosition)];
    return this.parameters;
  }

  public getConfig(): IConfig {
    return this.config;
  }

  public setConfig(config: IConfig): void {
    this.config = config;
  }

  public getParameters(): IParameters {
    return this.parameters;
  }

  public setTrackStart(trackStart: number = 0): void {
    this.trackStart = trackStart;
  }

  public setTrackWidth(trackWidth: number | undefined): void {
    this.trackWidth = trackWidth === undefined ? 500 : trackWidth;
  }

  public getStepsArr(): IStepsArr[] {
    return this.stepsArr;
  }

  static takeClosestNum(num: number, array: number[]): number {
    const indexOfClosest = Model.takeClosestIndex(num, array);
    return array[indexOfClosest];
  }

  static takeClosestIndex(num: number, array: number[]): number {
    const closest = array.reduce((a, b) => (Math.abs(b - num) < Math.abs(a - num) ? b : a));
    return array.indexOf(closest);
  }

  static round(num: number): number {
    return (Math.round(num * 10)) / 10;
  }

  private correctOptionsType(options: IOptions = this.options):IOptions {
    const correctOptions = { ...options };
    correctOptions.max = Number.isInteger(options.max) ? Math.round(options.max) : defaults.max;
    correctOptions.min = Number.isInteger(options.min) ? Math.round(options.min) : defaults.min;
    correctOptions.step = Number.isInteger(options.step) ? Math.round(options.step) : defaults.step;
    correctOptions.from = Number.isInteger(options.from) ? Math.round(options.from) : defaults.from;
    correctOptions.to = Number.isInteger(options.to) ? Math.round(options.to) : defaults.to;
    correctOptions.vertical = typeof options.vertical === 'boolean' ? options.vertical : defaults.vertical;
    correctOptions.tip = typeof options.tip === 'boolean' ? options.tip : defaults.tip;
    correctOptions.range = typeof options.range === 'boolean' ? options.range : defaults.range;
    return correctOptions;
  }

  private takeXByValue(val: number): number {
    const position = this.stepsArr.find((el) => el.value === val);
    if (position) {
      return position.x;
    }

    throw new Error('position for this value is not consist');
  }

  private takeValueByX(x: number): number {
    const item = this.stepsArr.find((el) => el.x === x);
    if (item) {
      return item.value;
    }

    throw new Error('value for this position is not consist');
  }
}

export default Model;

import Observer from '../../../observer/Observer';
import IObserver from '../../../observer/interface';
import IHandle from './interface';

class Handle implements IHandle {
  public observer: IObserver;

  readonly $slider: JQuery<HTMLElement>;

  readonly $track: JQuery<HTMLElement>;

  private vertical: boolean;

  private handles: JQuery<HTMLElement>[];

  constructor(slider: JQuery<HTMLElement>, vertical: boolean) {
    this.$slider = slider;
    this.$track = this.$slider.find('.meta-slider__track');
    this.vertical = vertical;
    this.observer = new Observer();
    this.handles = this.initHandles();
    this.bindEventListeners();
  }

  public correctHandlesByRange(range: boolean): void {
    if (range && this.handles.length === 1) {
      const $handle2 = jQuery('<div>', {
        class: 'meta-slider__handle meta-slider__handle_right',
      }).appendTo(this.$track);
      this.handles.push($handle2);
    } else if (!range && this.handles.length === 2) {
      const $handle2 = this.handles[1];
      $handle2.remove();
      this.handles.pop();
    }
    this.bindEventListeners();
  }

  public moveHandles(positions: number[]): void {
    this.handles.forEach((item, index) => {
      item.css(this.vertical ? 'top' : 'left', `${positions[index] - 20 / 2}px`);
      item.css(this.vertical ? 'left' : 'top', '-5px');
    });
  }

  public setVertical(vertical: boolean): void {
    this.vertical = vertical;
  }

  static handleDragStart(): boolean {
    return false;
  }

  private bindEventListeners(): void {
    this.handles.forEach((item) => {
      item.on('mousedown touchstart', this.handleHandleMouseDown.bind(this));
    });
  }

  private handleHandleMouseDown(event: Event): void {
    event.preventDefault();
    const eventTarget = <Element>event.target;
    $(document).on('mousemove', this.handleMouseMove.bind(this, eventTarget));
    $(document).on('touchmove', this.handleTouchMove.bind(this, eventTarget));
    $(document).on('mouseup touchend', this.handleMoveEnd.bind(this));
    $(document).on('dragstart', Handle.handleDragStart);
  }

  private handleMouseMove(eventTarget: EventTarget, event: Event): void {
    const index = $(eventTarget).hasClass('meta-slider__handle_right') ? 1 : 0;
    const eventPosition = this.vertical ? (<MouseEvent>event).pageY : (<MouseEvent>event).pageX;
    const options = { eventPosition, index };
    this.observer.notify('mouseMove', options);
  }

  private handleTouchMove(eventTarget: EventTarget, event: Event): void {
    const index = $(eventTarget).hasClass('meta-slider__handle_right') ? 1 : 0;
    const touches = (<TouchEvent>event)?.touches;
    if (touches !== undefined) {
      const touch = touches[0];
      const eventPosition = this.vertical ? touch.pageY : touch.pageX;
      const options = { eventPosition, index };
      this.observer.notify('mouseMove', options);
    }
  }

  private handleMoveEnd(event: Event): void {
    this.observer.notify('moveEnd', event);
    $(document).off('mousemove mouseup touchmove touchend');
  }

  private initHandles(): JQuery<HTMLElement>[] {
    const handles: JQuery<HTMLElement>[] = [];
    const $handle1 = jQuery('<div>', {
      class: 'meta-slider__handle meta-slider__handle_left',
    }).appendTo(this.$track);
    handles.push($handle1);
    const $handle2 = jQuery('<div>', {
      class: 'meta-slider__handle meta-slider__handle_right',
    }).appendTo(this.$track);
    handles.push($handle2);
    return handles;
  }
}

export default Handle;

import IInterval from './interface';

class Interval implements IInterval {
  readonly $slider: JQuery<HTMLElement>;

  readonly $interval: JQuery<HTMLElement>;

  readonly $track: JQuery<HTMLElement>;

  constructor(slider: JQuery<HTMLElement>) {
    this.$slider = slider;
    this.$track = $(this.$slider).find('.meta-slider__track');
    jQuery('<div>', {
      class: 'meta-slider__interval',
    }).appendTo(this.$track);
    this.$interval = this.$slider.find('.meta-slider__interval');
  }

  public moveInterval(positions: number[], vertical: boolean): void {
    let min: number;
    let width: number;
    const handleWidth = 20;
    const gap = 2; // to make a gap between interval and handle
    if (positions.length === 1) {
      min = 0;
      width = positions[0] - handleWidth / 2 - gap;
    } else if (positions.length === 2) {
      const minHandle = Math.min(positions[0], positions[1]);
      const maxHandle = Math.max(positions[0], positions[1]);
      min = minHandle + handleWidth / 2;
      width = maxHandle - minHandle - handleWidth - gap;
    } else {
      throw new Error('wrong number of handles');
    }

    width = (width > 0) ? width : 0;
    this.$interval.css(vertical ? 'height' : 'width', `${width}px`);
    this.$interval.css(vertical ? 'width' : 'height', '10px');
    this.$interval.css(vertical ? 'top' : 'left', `${min}px`);
    this.$interval.css(vertical ? 'left' : 'top', '0px');
  }
}

export default Interval;

import { IStepsArr, IScaleArr } from '../../../interfaces/interfaces';
import Observer from '../../../observer/Observer';
import IObserver from '../../../observer/interface';
import IScale from './interface';

class Scale implements IScale {
  public observer: IObserver;

  readonly $slider: JQuery<HTMLElement>;

  readonly $scale: JQuery<HTMLElement>;

  private vertical: boolean;

  constructor(slider: JQuery<HTMLElement>, vertical: boolean) {
    this.$slider = slider;
    this.vertical = vertical;
    this.observer = new Observer();
    this.$scale = jQuery('<div>', {
      class: 'meta-slider__scale',
    }).appendTo(this.$slider);
    this.bindEventListeners();
  }

  public initScale(stepsArr: IStepsArr[], vertical:boolean = false): void {
    this.vertical = vertical;
    this.$scale.empty();
    const valuesArr = Scale.takeValues(stepsArr);
    this.addValues(valuesArr);
  }

  public setVertical(vertical: boolean): void {
    this.vertical = vertical;
  }

  static takeValues(stepsArr: IStepsArr[]): IScaleArr[] {
    const scaleArr: IScaleArr[] = [];
    const emptyStepsLength = Math.round(stepsArr.length / 10);
    const isStepsArrLengthBig = stepsArr.length > 10;
    stepsArr.forEach((item, i) => {
      const isLineInStep = i % emptyStepsLength === 0;
      const isValueSuitable = isStepsArrLengthBig && isLineInStep;
      const isAddValue = isValueSuitable || !isStepsArrLengthBig;
      if (isAddValue) {
        scaleArr.push({ item, index: i });
      }
    });
    if (scaleArr[scaleArr.length - 1].item !== stepsArr[stepsArr.length - 1]) {
      scaleArr.pop();
      scaleArr.push({ item: stepsArr[stepsArr.length - 1], index: stepsArr.length - 1 });
    }
    return scaleArr;
  }

  private bindEventListeners(): void {
    $(this.$slider).on('click touchstart', this.sendScaleClickValue.bind(this));
  }

  private sendScaleClickValue(event: Event): void {
    const { observer } = this;
    if ((<HTMLInputElement>event.target).dataset.value) {
      const currentValue = Number((<HTMLInputElement>event.target).dataset.value);
      observer.notify('click', currentValue);
    }
  }

  private addValues(valuesArr: { item: IStepsArr, index: number }[]): void {
    valuesArr.forEach((item) => {
      const line = jQuery('<div>', {
        class: 'meta-slider__line',
        text: this.vertical ? '\u2014' : '|',
        style: this.vertical ? `top: ${item.item.x - 10}px` : `left: ${item.item.x - 10}px`,
      }).appendTo(this.$scale);
      jQuery('<div>', {
        class: 'meta-slider__value js-meta-slider__value',
        'data-value': item.item.value,
        text: item.item.value,
      }).appendTo(line);
    });
  }
}

export default Scale;

import ITip from './interface';

class Tip implements ITip {
  readonly $slider: JQuery<HTMLElement>;

  readonly $tip: JQuery<HTMLElement>;

  private $tips: JQuery<HTMLElement>[];

  constructor(slider: JQuery<HTMLElement>) {
    this.$slider = slider;
    this.$tips = [];
    this.$tip = jQuery('<div>', {
      class: 'meta-slider__tip',
    });
  }

  public initTips(tip: boolean = true): void {
    const element = this;
    this.$slider.find('.meta-slider__tip').remove();
    this.$tips = [];
    if (tip) {
      const $handles = this.$slider.find('.meta-slider__handle');
      $handles.each(function () {
        const $tipClone = element.$tip.clone();
        $tipClone.appendTo($(this));
        element.$tips.push($tipClone);
      });
    }
  }

  public changeTips(values: number[]): void {
    this.$tips.forEach((item, index) => {
      item.html(`${values[index]}`);
    });
  }
}

export default Tip;
